import React, { useRef, useEffect } from "react";

function Tree_2() {
    const tree2Ref = useRef(null);

    useEffect(() => {
        const tree2Element = tree2Ref.current;//текуший элемент div для которого нужно получить размер
        if (tree2Element) {
            //const { width,height, x,y} = divElement.getBoundingClientRect();
            // getBoundingClientRect() возвращает объект с размерами элемента
            const width = tree2Element.offsetWidth;
            console.log("Размер Tree_2:", width);
        }
    }, []);

    return (
        <div ref={tree2Ref}>
            <img id="tree_2" src="/Icons/Landscape/tree_1.png" />
        </div>
    );
}

export default Tree_2;
