import React, { useEffect, useState, useCallback, useMemo } from "react";
import ObjectCreator from "./ObjectCreator";
import Hero from "./Hero";
import ObjectCreatorEarth from "./ObjectCreatorEarth";
import ObjectCreatorGrass from "./ObjectCreatorGrass";
import ObjectCreatorBird from "./ObjectCreatorBird";
import "./style/AllRoad.css";
import RoadMove from "./RoadMove";

const ObjectCreatorBirdMemo = React.memo(ObjectCreatorBird);
const HeroMemo = React.memo(Hero);
const ObjectCreatorGrassMemo = React.memo(ObjectCreatorGrass);
const ObjectCreatorEarthMemo = React.memo(ObjectCreatorEarth);

function AllRoad({ Road }) {
  const [road, setRoad] = useState(() => Road); // Используем функцию-инициализатор для установки начального значения

  const animate = useCallback(() => {
    setRoad((prevRoad) => RoadMove(prevRoad));
  }, [Road]);

  const [previousTime, setPreviousTime] = useState(0);

  useEffect(() => {
    let animationFrameId;

    const animate = (time) => {
      if (time - previousTime >= 500) {
        setRoad((prevRoad) => RoadMove(prevRoad));
        setPreviousTime(time);
      }
      animationFrameId = requestAnimationFrame(animate);
    };

    animationFrameId = requestAnimationFrame(animate);

    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, [previousTime]);

  const windowWidth = window.innerWidth;
  const objectCount = Math.floor(windowWidth / 40);

  const memoizedRoad = useMemo(() => road, [road]); // Мемоизируем значение road

  return (
      <div className="AllRoadContainer">
        {Array.from({ length: 8 }).map((_, rowIndex) => (
            <div key={rowIndex} className="RowContainer">
              {Array.from({ length: objectCount }).map((_, objectIndex) => {
                let component;

                if (rowIndex === 0) {
                  component = <ObjectCreatorBirdMemo objectIndex={memoizedRoad[objectIndex]} />;
                } else if (rowIndex === 1) {
                  if (objectIndex === 3) {
                    component = <HeroMemo />;
                  } else {
                    component = <ObjectCreator objectIndex={memoizedRoad[objectIndex]} />;
                  }
                } else if (rowIndex === 2) {
                  component = <ObjectCreatorGrassMemo objectIndex={memoizedRoad[objectIndex]} />;
                } else if (rowIndex === 3) {
                  component = <ObjectCreatorEarthMemo objectIndex={memoizedRoad[objectIndex]} />;
                }

                return (
                    <div key={objectIndex} className="PixelContainer">
                      {component}
                    </div>
                );
              })}
            </div>
        ))}
      </div>
  );
}

export default AllRoad;
