import React, { useEffect, useState } from "react";
import ObjectCreator from "./ObjectCreator";
import Hero from "./Hero";
import ObjectCreatorEarth from "./ObjectCreatorEarth";
import ObjectCreatorGrass from "./ObjectCreatorGrass";
import ObjectCreatorBird from "./ObjectCreatorBird";
import "./style/AllRoad.css";
import RoadMove from "./RoadMove";

function AllRoad({ Road }) {
    const [road, setRoad] = useState(Road);
    const [previousTime, setPreviousTime] = useState(0);
    const [speed, setSpeed] = useState(1); // Задайте желаемую скорость анимации

    useEffect(() => {
        let animationFrameId;

        const animate = (time) => {
            if (time - previousTime >= 1000 / speed) {
                setRoad((prevRoad) => RoadMove(prevRoad));
                setPreviousTime(time);
            }
            animationFrameId = requestAnimationFrame(animate);
        };

        animationFrameId = requestAnimationFrame(animate);

        return () => {
            cancelAnimationFrame(animationFrameId);
        };
    }, [previousTime, speed]);

    const windowWidth = window.innerWidth;
    const objectCount = Math.floor(windowWidth / 40); // Вычисляем количество элементов массива

    return (
        <div className="AllRoadContainer">
            {Array.from({ length: 4 }).map((_, rowIndex) => (
                <div key={rowIndex} className="RowContainer">
                    {Array.from({ length: objectCount }).map((_, objectIndex) => {
                        let component;

                        if (rowIndex === 0) {
                            component = <ObjectCreatorBird objectIndex={road[objectIndex]} />;
                        } else if (rowIndex === 1) {
                            if (objectIndex === 3) {
                                component = <Hero />;
                            } else {
                                component = <ObjectCreator objectIndex={road[objectIndex]} />;
                            }
                        } else if (rowIndex === 2) {
                            component = <ObjectCreatorGrass objectIndex={road[objectIndex]} />;
                        } else if (rowIndex === 3) {
                            component = <ObjectCreatorEarth objectIndex={road[objectIndex]} />;
                        }

                        return (
                            <div key={objectIndex} className="PixelContainer">
                                {component}
                            </div>
                        );
                    })}
                </div>
            ))}
        </div>
    );
}

export default AllRoad;
