import React, { useEffect, useState, useRef } from "react";
import RoadGenerator from "./RoadGenerator";
import Hero from "./Hero";
import ObjectGeneratorTop from "./ObjectGeneratorTop";
import ObjectGeneratorEarth from "./ObjectGeneratorEarth";
import ObjectGeneratorGrass from "./ObjectCreatorGrass";
import ObjectGeneratorMid from "./ObjectGeneratorMid";
import "./style/AllRoad.css";

let personNeedJump = false;
function AllRoad() {

  const roadGenerator = new RoadGenerator();
  const [road, setRoad] = useState(roadGenerator.getContent());
  const divRef = useRef(null);
  const heroRef = useRef(null);
  const ObjectCreatorBirdMemo = React.memo(ObjectGeneratorTop);
  const HeroMemo = React.memo(Hero);
  const ObjectCreatorGrassMemo = React.memo(ObjectGeneratorGrass);

  let heroOffset = 0;
  let cellSize = null;
  let divOffset = 0;
  let needNextUpdate = true;
  let heroTopIndex = 0;

  function getDivSize(getDivSizeAction) {
    if (getDivSizeAction() !== null) {
      cellSize = getDivSizeAction();
    }
  }
  function SetDivMove(time){
    setTimeout(()=>{
      if (divRef.current === null || divRef===null || heroRef === null) {
        SetDivMove(time);
        return;
    }
      setTimeout(()=>{

        if(heroTopIndex <= -80){ //80
          personNeedJump = false;
        }

        if (personNeedJump === true && heroTopIndex !== -80){ //160
          heroTopIndex -= 2;
        }
        else if (personNeedJump === false && heroTopIndex !== 0) { //160
          heroTopIndex += 2;
        }
      }, 50);
      divOffset -= 1;
      heroOffset += 1;

      if (divOffset <= -cellSize) {
        divOffset += cellSize;
        needNextUpdate = false;
        setRoad(Road.RoadMoveLogic(road));
      }

      heroRef.current.style.top = `${heroTopIndex}px`;

      if(needNextUpdate){
        heroRef.current.style.left = `${divOffset}px`;
        heroRef.current.style.left = `${heroOffset}px`;
        SetDivMove(time);
      }
    }, time)
  }
  useEffect(()=>{
    return ()=>{
      heroRef.current.style.left = `0px`; //450
      divRef.current.style.left = `${divOffset}px`;
    }
  })
  SetDivMove(10);
  let JumpEvent = (event)=> {
    personNeedJump = true;
}
  const windowWidth = window.innerWidth;
  const objectCount = Math.floor(windowWidth / 35);
  return (
      <div className="AllRoadContainer">
        {Array.from({ length: 20 }).map((_, rowIndex) => (
            <div key={rowIndex} className="RowContainer">
              {Array.from({ length: objectCount }).map((_, objectIndex) => {
                let component;

                if (rowIndex === 13) {
                  component = (
                      <div>
                        <ObjectCreatorBirdMemo getDivAction={getDivSize} objectIndex={road[0]} objectIndex={road[objectIndex]} />
                      </div>
                  );
                } else if (rowIndex === 14) {
                  if (objectIndex === 5) {
                    component = (
                        <div>
                          <HeroMemo heroRef={heroRef} />
                        </div>
                    );
                  } else {
                    component = (
                        <div>
                          <ObjectGeneratorMid getDivAction={getDivSize} objectIndex={road[objectIndex]} />
                        </div>
                    );
                  }
                } else if (rowIndex === 15) {
                  component = (
                      <div>
                        <ObjectCreatorGrassMemo getDivAction={getDivSize} objectIndex={road[objectIndex]} />
                      </div>
                  );
                }

                return (
                    <div key={objectIndex} className="PixelContainer">
                      {component}
                    </div>
                );
              })}
            </div>
        ))}
      </div>
  );
}

export default AllRoad;
