import React, { useEffect, useRef } from "react";
import Bird from "./Bird";
import Empty from "./Empty";

const ObjectCreatorBird = React.forwardRef(({ objectIndex }, ref) => {
    const objectCreatorBirdRef = useRef(null);
    useEffect(() => {
        const measureWidth = () => {
            const objectCreatorElement = objectCreatorBirdRef.current;
            if (objectCreatorElement) {
                const { width } = objectCreatorElement.getBoundingClientRect();
                objectCreatorElement.style.width = `${width}px`;
            }
        };

        const timeoutId = setTimeout(measureWidth, 0);

        return () => clearTimeout(timeoutId);
    }, []);

    if (objectIndex >= 0 && objectIndex <= 94) {
        return <Empty ref={objectCreatorBirdRef} />;
    } else if (objectIndex >= 95 && objectIndex <= 99) {
        return <Bird ref={objectCreatorBirdRef} />;
    } else {
        return null;
    }
});

export default ObjectCreatorBird;
