import {useEffect, useRef} from "react";

function Grass() {
    const GrassRef = useRef(null);

    useEffect(() => {
        const GrassElement = GrassRef.current;
        if (GrassElement) {
            const width = GrassElement.offsetWidth;
            console.log("Размер Grass:", width);
        }
    }, []);
    return (
        <div ref={GrassRef}>
            <img src="/Icons/Landscape/grass.png" style={{ width: "100%", height: "100%" }}/>
        </div>
    )

}
export default Grass;
