import React, { useRef, useEffect } from "react";

function Tree_2() {
    const divRef = useRef(null);

    useEffect(() => {
        const divElement = divRef.current;//текуший элемент div для которого нужно получить размер
        if (divElement) {
            //const { width,height, x,y} = divElement.getBoundingClientRect();
            // getBoundingClientRect() возвращает объект с размерами элемента
            const width = divElement.offsetWidth;
            console.log("Размер Tree_2:", width);
        }
    }, []);

    return (
        <div ref={divRef}>
            <img id="tree_2" src="/Icons/Landscape/tree_1.png" />
        </div>
    );
}

export default Tree_2;
